mapper.select:
select * from products
WHERE products.is_delete = 0 ORDER BY create_at DESC
limit #{page},#{size}

select * from products WHERE products.is_delete = 0 And products.name like CONCAT('%', #{name}, '%')
And products.active = #{active}
and products.prince BETWEEN #{princefrom} AND #{princeto}
limit #{page},#{size}

select * from products WHERE products.is_delete = 0 And products.name like CONCAT('%', #{name}, '%')
And products.active = #{active}
and products.prince BETWEEN #{princefrom} AND #{princeto}

mapper.update:
UPDATE products SET products.is_delete = true
WHERE
products.id = #{id}
