mapper.select:
SELECT * FROM users
where
is_delete = 0 ORDER BY create_at DESC Limit #{page},#{size}

SELECT id, name, email, group_role, status FROM users WHERE is_delete = 0 and users.email like CONCAT('%', #{user.email}, '%')
And users.name like CONCAT('%', #{user.name}, '%')
And users.group_role like CONCAT('%', #{user.groupRole}, '%')
And users.status = #{user.status}
Limit #{page},#{size}

SELECT id, name, email, group_role, status FROM users WHERE is_delete = 0 and users.email like CONCAT('%', #{user.email}, '%') And users.name like CONCAT('%', #{user.name}, '%')
And users.group_role like CONCAT('%', #{user.groupRole}, '%')
And users.status = #{user.status}

mapper.update:
UPDATE users
SET users.login_date = #{user.loginDate},
users.last_iplogin =
#{user.lastIpLogin}
WHERE users.email =
#{user.email}

UPDATE users
SET users.remember_token =
#{user.rememberToken}
WHERE users.email = #{user.email}

UPDATE users SET users.name = #{user.name},
users.email = #{user.email}, users.group_role = #{user.groupRole},
users.status = #{user.status} WHERE users.id = #{user.id}
